/**
 * Tagged template literal dedent function that also unwraps lines.
 * Double newlines become a single newline.
 */
export declare function dedent(strings: TemplateStringsArray, ...values: unknown[]): string;
/**
 * Builds the Postgres database URL for the given parameters.
 */
export declare function buildDatabaseURL(opts: {
    user: string;
    password: string;
    host: string;
    port: number;
    dbName: string;
    ssl?: boolean;
}): string;
